-- Deploy wavdb:add_tekelek_records to pg
-- requires: create_tekelek_tbl

BEGIN;


UPDATE wav_schema.tekelek
SET last_collected = CURRENT_DATE - (1 + RANDOM() * 2)::INTEGER + (RANDOM() * 19)::INTEGER * interval '1 hour' + (RANDOM() * 59)::INTEGER * interval '1 minute' + (RANDOM() * 59)::INTEGER * interval '1 second'
WHERE last_collected IS NULL;


COMMIT;
